import React, { Component } from 'react';
// import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Navigation from './components/navigation/navigation.js';
import SignIn from './components/signin/signin.js';
import Register from './components/register/register.js';
import PartnerSearch from './components/partnersearch/partnersearch.js';
import Home from './components/home/home.js';
import ChatWrapper from './components/chat/chatwrapper.js'
import LoggedOut from './components/loggedout/loggedout.js'
// import MessageList from './components/chat/messagelist.js';
// import SendMessageForm from './components/chat/sendmessageform.js';
// import RoomList from './components/chat/roomlist.js';
// import NewRoomForm from './components/chat/newroomform.js';


import './App.css';


class App extends Component {
  constructor () {
    super();
    this.state = {
      input : '',
      route: 'signin',
      isSignedIn: false,
      user: {
        username: '',
        email:'',
        location: '',
        experienceLevel: ''
      }

    }
    this.onUsernameSubmitted = this.onUsernameSubmitted.bind(this)
    this.onRouteChange = this.onRouteChange.bind(this)
    this.loadUser = this.loadUser.bind(this)
    }


// load user info
loadUser = (data) => {
  this.setState({user: {
    username: data.username,
    email: data.email,
    experienceLevel: data.experiencelevel,
    location: data.location
  }})
}

onUsernameSubmitted(username) {
   fetch('https://damp-forest-34333.herokuapp.com/users', {
    method: 'POST',
    headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username }),
    })
      .then(response => {
        this.setState({
          username: username
        })
        console.log(username);
      })
      .catch(error => console.error('error', error))
  }

// route change function
  onRouteChange = (route) => {
    if(route === 'signout'){
      this.setState({isSignedIn: false})
    } else if (route === 'home') {
      this.setState({isSignedIn: true})
    }
    this.setState({route: route});
    console.log(route);
  }


// begin render
  render() {
    const {isSignedIn, route} = this.state;
    return (
      <div>
          {(() => {
            switch (route) {
            case 'signin':
              return <SignIn loadUser={this.loadUser} onRouteChange = {this.onRouteChange} />;
            case 'register':
              return <Register loadUser={this.loadUser} onRouteChange = {this.onRouteChange} username = {this.state.user.username} submitUser = {this.onUsernameSubmitted} />;
            case 'home':
                return <Home
                    className="col-12"
                    username = {this.state.user.username}
                    location = {this.state.user.location}
                    experienceLevel = {this.state.user.experienceLevel}
                />
            case 'search':
              return <PartnerSearch />
            case 'chat':
              return <ChatWrapper username = {this.state.user.username} />
            case 'signout':
              return <SignIn loadUser={this.loadUser} onRouteChange = {this.onRouteChange} />;
            default:
              <SignIn loadUser={this.loadUser} onRouteChange = {this.onRouteChange} />;

          }
        })()}


      </div>
    );
  }

}

export default App;
