import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
// import Navigation from './components/navigation/navigation.js';
import SignIn from './components/signin/signin.js';
import Register from './components/register/register.js';
import PartnerSearch from './components/partnersearch/partnersearch.js';
import Home from './components/home/home.js';
import ChatWrapper from './components/chat/chatwrapper.js'
// import LoggedOut from './components/loggedout/loggedout.js'
// import MessageList from './components/chat/messagelist.js';
// import SendMessageForm from './components/chat/sendmessageform.js';
// import RoomList from './components/chat/roomlist.js';
// import NewRoomForm from './components/chat/newroomform.js';


import './App.css';


class App extends Component {
  constructor () {
    super();
    this.state = {
      input : '',
      route: 'signin',
      isSignedIn: false,
      user: {
        username: '',
        email:'',
        location: '',
        experienceLevel: ''
      }

    }
    this.onUsernameSubmitted = this.onUsernameSubmitted.bind(this)
    this.onRouteChange = this.onRouteChange.bind(this)
    }


// load user info
loadUser = (data) => {
  this.setState({user: {
    username: data.username,
    email: data.email,
    experienceLevel: data.experiencelevel,
    location: data.location
  }})
}

onUsernameSubmitted(username) {
   fetch('https://damp-forest-34333.herokuapp.com/users', {
    method: 'POST',
    headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username }),
    })
      .then(response => {
        this.setState({
          username: username
        })
        console.log(username);
      })
      .catch(error => console.error('error', error))
  }

// route change function
  onRouteChange = (route) => {
    if(route === 'signout'){
      this.setState({isSignedIn: false})
    } else if (route === 'home') {
      this.setState({isSignedIn: true})
    }
    this.setState({route: route});
    console.log(route);
  }


// begin render
  render() {
    const {isSignedIn, route} = this.state;
    if (this.props.isSignedIn) {
    return (
      <Router>
          <div className = 'authenticate' >
              <nav className="navbar navbar-expand navbar-dark bg-dark">
                <img id="nav-logo" className="navbar-brand" src={require('./img/alpha-nav.png')} style={{width: '30px'}} alt="alpha-nav" />
                  <div className="collapse navbar-collapse" id="navbarToggleExternalContent">
                    <ul className="navbar-nav">
                      <li className="nav-item">
                        <Link className="nav-link" to='/'>Home</Link>
                      </li>
                      <li className="nav-item">
                        <Link className="nav-link" to='/search/'>Search</Link>
                      </li>
                      <li className="nav-item">
                        <Link className="nav-link" to='/chat/'>Chat</Link>
                      </li>
                      <li className="nav-item">
                        <p className="nav-link" onClick = {() => this.onRouteChange('signout')}>Sign Out</p>
                      </li>
                   </ul>
                 </div>
              </nav>
              <Route path="/" exact component={Home} />
              <Route path="/search/" component={PartnerSearch} />
              <Route path="/chat/" render={(props) => <ChatWrapper {...props} username = {this.state.user.username} />} />
          </div>
        </Router>
    );
  } else {
    return (
      <Router>
        <div className = 'authenticate' >
            <nav className="navbar navbar-expand navbar-dark bg-dark">
            <img id="nav-logo" className="navbar-brand" src={require('./img/alpha-nav.png')} style={{width: '30px'}} alt="alpha-nav" />
               <div className="collapse navbar-collapse" id="options">
                 <ul className="navbar-nav mr-auto">
                   <li className="nav-item">
                       <Link to='/signin/' className="nav-link" onClick = {() => this.onRouteChange('signin')} >
                         Sign In
                       </Link>
                  </li>
                   <li className="nav-item">
                   <Link to= '/register/' className= "nav-link" onClick = {() => this.onRouteChange('register')} >
                     Register
                   </Link>
                  </li>
                 </ul>
               </div>
            </nav>
            <Route path="/signin/" render={(props) => <SignIn {...props} loadUser={this.loadUser} onRouteChange = {this.onRouteChange} />} />
            <Route path="/register/" render={(props) => <Register {...props}  loadUser={this.loadUser} onRouteChange = {this.onRouteChange} username = {this.state.user.username} submitUser = {this.onUsernameSubmitted} />} />
        </div>
      </Router>

    );
  }
}
}

export default App;
