import React, { Component } from 'react';
import Navigation from './components/navigation/navigation.js';
import SignIn from './components/signin/signin.js';
import Register from './components/register/register.js';
import PartnerSearch from './components/partnersearch/partnersearch.js';
import Home from './components/home/home.js';
import { ChatManager, TokenProvider } from '@pusher/chatkit-client';
import MessageList from './components/chat/messagelist.js';
import SendMessageForm from './components/chat/sendmessageform.js';
import RoomList from './components/chat/roomlist.js';
import NewRoomForm from './components/chat/newroomform.js';


import './App.css';

import {instanceLocator, tokenUrl, chatKey} from './config.js'



class App extends Component {
  constructor () {
    super();
    this.state = {
      input : '',
      route: 'signin',
      isSignedIn: false,
      user: {
        username: '',
        email:'',
        location: '',
        experienceLevel: ''
      }

    }

    }


// load user info
loadUser = (data) => {
  this.setState({user: {
    username: data.username,
    email: data.email,
    experienceLevel: data.experiencelevel,
    location: data.location
  }})
}


// route change function
  onRouteChange = (route) => {
    if(route === 'signout'){
      this.setState({isSignedIn: false})
    } else if (route === 'home') {
      this.setState({isSignedIn: true})
    }
    this.setState({route: route});
  }


// begin render
  render() {
    const {isSignedIn, route} = this.state;
    return (
      <div className="App">
          <header className="App-header">
            <Navigation isSignedIn = {isSignedIn} onRouteChange = {this.onRouteChange} />
            <h1 className="alpha">Î±</h1>
          </header>
          {this.state.route === 'home'
            ? <div>
              <Home
                  username = {this.state.user.username}
                  location = {this.state.user.location}
                  experienceLevel = {this.state.user.experienceLevel}
              />
              <PartnerSearch />
              <ChatWrapper />
            </div>
            : (
              route === 'signin'
              ? <SignIn loadUser={this.loadUser} onRouteChange = {this.onRouteChange} />
              : <Register loadUser={this.loadUser} onRouteChange = {this.onRouteChange} username = {this.state.user.username} />
            )
          }

      </div>
    );
  }
}

export default App;
