import React, { Component } from 'react';
import Navigation from './components/navigation/navigation.js';
import SignIn from './components/signin/signin.js';
import Register from './components/register/register.js';
import PartnerSearch from './components/partnersearch/partnersearch.js';
import Home from './components/home/home.js';
import Chatkit from '@pusher/chatkit-client';
import MessageList from './components/chat/messagelist.js';
import SendMessageForm from './components/chat/sendmessageform.js';
import RoomList from './components/chat/roomlist.js';
import NewRoomForm from './components/chat/newroomform.js';


import './App.css';

import {instanceLocator, tokenUrl, chatKey} from './config.js'



class App extends Component {
  constructor () {
    super();
    this.state = {
      input : '',
      route: 'signin',
      isSignedIn: false,
      user: {
        username: '',
        email:'',
        location: '',
        experienceLevel: ''
      },
        roomId: null,
        messages: [],
        joinableRooms:[],
        joinedRooms: []

    }
      this.sendMessage = this.sendMessage.bind(this);
      this.subscribeToRoom = this.subscribeToRoom.bind(this);
      this.getRooms = this.getRooms.bind(this);
      this.createRoom = this.createRoom.bind(this)
    }


// load user info
loadUser = (data) => {
  this.setState({user: {
    username: data.username,
    email: data.email,
    experienceLevel: data.experiencelevel,
    location: data.location
  }})
}

// form change functions
  // onInputChange = (event) => {
  //   this.setState({input: event.target.value});
  // }

// route change function
  onRouteChange = (route) => {
    if(route === 'signout'){
      this.setState({isSignedIn: false})
    } else if (route === 'home') {
      this.setState({isSignedIn: true})
    }
    this.setState({route: route});
  }

  componentDidMount(){
    const chatManager = new Chatkit.ChatManager({
      instanceLocator,
      userId: 'joeclimber',
      // this.state.user.username,
      tokenProvider: new Chatkit.TokenProvider({
        url: tokenUrl
      })

      // key: chatKey
      })

    chatManager.connect()
    .then(currentUser => {
      currentUser.subscribeToRoom({
        roomId: currentUser.rooms[0].id,
        hooks: {
          onMessage: message => {
            console.log(`message: ${message.text}`)
          }
        }
      });
      // this.currentUser
      // = currentUser
      // this.getRooms()
      // this.subscribeToRoom()


    })
    .catch(err => console.log('error on connecting', err))
  }


  getRooms() {
  this.currentUser.getJoinableRooms()
  .then(joinableRooms => {
    this.setState({
      joinableRooms,
      joinedRooms: this.currentUser.rooms
    })
  })
  .catch(err => console.log('error on joinable rooms', err))
  }

  subscribeToRoom(roomId) {
  this.setState({ messages:[] })
  this.currentUser.subscribeToRoom({
    roomId:roomId,
    hooks: {
      onNewMessage: message => {
        this.setState({
          messages: [...this.state.messages, message]
        })
      }
    }
  })
  .then(room => {
    this.setState({
      roomId: room.id
    })
    this.getRooms();
  })
  .catch(err => console.log('error subscribing to room', err))
  }

  sendMessage(text) {
  this.currentUser.sendMessage({
    text,
    roomId:this.state.roomId

  })
  }

  createRoom(name) {
      this.currentUser.createRoom({
            name
        })
        .then(room => this.subscribeToRoom(room.id))
        .catch(err => console.log('error with createRoom: ', err))
  }

// begin render
  render() {
    const {isSignedIn, route} = this.state;
    return (
      <div className="App">
          <header className="App-header">
            <Navigation isSignedIn = {isSignedIn} onRouteChange = {this.onRouteChange} />
            <h1 className="alpha">Î±</h1>
          </header>
          {this.state.route === 'home'
            ? <div>
              <Home
                  username = {this.state.user.username}
                  location = {this.state.user.location}
                  experienceLevel = {this.state.user.experienceLevel}
              />
              <PartnerSearch />
              <RoomList
                roomId = {this.state.roomId}
                subscribeToRoom={this.subscribeToRoom}
                rooms={[...this.state.joinableRooms, ...this.state.joinedRooms]} />
              <MessageList
                roomId={this.state.roomId}
                messages={this.state.messages} />
              <SendMessageForm
              disabled={!this.state.roomId}
              sendMessage={this.sendMessage} />
              <NewRoomForm createRoom= {this.createRoom} />
            </div>
            : (
              route === 'signin'
              ? <SignIn loadUser={this.loadUser} onRouteChange = {this.onRouteChange} />
              : <Register loadUser={this.loadUser} onRouteChange = {this.onRouteChange} username = {this.state.user.username} />
            )
          }

      </div>
    );
  }
}

export default App;
